import React, {useState} from "react";
import "./styles.css";

function App() {
const sixRandomColors = [];
const [userPass,setUserPass] = useState()


function getSixColors(){
  for (var i=0;i<6;i++){
    sixRandomColors[i] = [Math.floor((Math.random()*256)),Math.floor((Math.random()*256)),Math.floor((Math.random()*256))]
    // getColorsArray(sixRandomColors)
  }
}
getSixColors()

let showColor = sixRandomColors[Math.floor(Math.random()*6)]
const displayColor =`rgb(${showColor[0]},${showColor[1]},${showColor[2]})`
const showColorIndex = sixRandomColors.indexOf(showColor)


// function getColorsArray(array){
//   const sixRandomColorsArray = array.map(x=>`rgb(${x[0]},${x[1]},${x[2]})`)
// }


function getIndexValue(id){
  const showColorIndex = sixRandomColors.indexOf(showColor)
  // console.log("picked color is "+id)
  // console.log("correct color is "+showColorIndex)
  let score = 0

  if (id === showColorIndex){
    setUserPass(true)
    getSixColors()
    score = score+1;
  } else if (id !== showColorIndex){
    setUserPass(false)
    getSixColors()

  }
}


  return (
    <main>
      <header data-testid="gameInstructions" className="header">Guess the Color</header>

      <div>
        <div>Score <span className="score">{score}</span></div>
        <div>Reset</div>
      </div>

      <div>
        <div style={{backgroundColor:displayColor}} className="colorBox" data-testid="colorBox">
        Display
        </div>
      </div>

      <div className="gameInfo" data-testid="gameStatus">
      </div>

      <div className="colorOptionsContainer">
      {sixRandomColors.map((x,index)=>(
      <div onClick={()=>getIndexValue(index)}
      key={index}
      id={index}
      style={{backgroundColor:`rgb(${x[0]},${x[1]},${x[2]})`}} className="colorPicks" data-testid="colorOption">
      <h1>Hello</h1>
      </div>
      ))}
      </div>

    </main>
  );
}
export default App;
