import React, {useState} from "react";
import "./styles.css";

function App() {
  const sixRandomColors = [];
const [userPass,setUserPass] = useState();
const [wins,setwins] = useState(0);
const [displayMessage,setDisplayMessage] = useState("");
const [restart,setRestart] = useState(false)
const [shake, setShake] = useState(false);

function getSixColors(){
  for (var i=0;i<6;i++){
    sixRandomColors[i] = [Math.floor((Math.random()*256)),Math.floor((Math.random()*256)),Math.floor((Math.random()*256))]
  }
}
userPass!==3&&getSixColors()

let showColor = sixRandomColors[Math.floor(Math.random()*6)]
const displayColor =`rgb(${showColor[0]},${showColor[1]},${showColor[2]})`
const showColorIndex = sixRandomColors.indexOf(showColor)


function getIndexValue(id){

  const showColorIndex = sixRandomColors.indexOf(showColor)
  if (id === showColorIndex){
    setUserPass(true)
    setTimeout(() => setUserPass(), 500);
    setwins(prev=>{
      return prev+1;
    })
    setDisplayMessage("Congrats, you win &#128509")


  } else if (id !== showColorIndex){
    setUserPass(1)
    // getSixColors()
    setwins(prev=>{
      return 0;
    })
    setDisplayMessage("Sorry, You Lost")
  }

  setShake(true);
  setTimeout(() => setShake(false), 500);

}
console.log(userPass)

  function restartAction(){
    setRestart(true)
    setwins(0)
    setUserPass(false)
    setDisplayMessage("")

  }

  return (
    <main>
      <header data-testid="gameInstructions" className="header">GUESS THE COLOR</header>

      <div className="scoreSection">
        <button>Score <span className="score">{wins}</span></button>
        <button onClick={restartAction}>Reset</button>
      </div>

      <div>
        <div style={{backgroundColor:displayColor}} className="colorBox" data-testid="colorBox">
        </div>
      </div>

      <div className={shake ? 'shake' : ''} data-testid="gameStatus">
      <p className="status">{displayMessage}</p>
      </div>

      <div className="colorOptionsContainer">
      {sixRandomColors.map((x,index)=>(
      <div onClick={()=>getIndexValue(index)}
      key={index}
      id={index}
      style={{backgroundColor:`rgb(${x[0]},${x[1]},${x[2]})`}} className="colorPicks" data-testid="colorOption">
      </div>
      ))}
      </div>

    </main>
  );
}
export default App;
